mapper.update:
update dish


        name = #{fl.name},
        value = #{fl.value}

    where dish_id = #{fl.dishId}

update dish
name = #{name}, image = #{image}, price = #{price}, status = #{status}, category_id = #{categoryId}, description = #{description},
where id = #{id}

mapper.select:
select d.id, d.name, d.price, d.image, d.description, d.status, d.update_time, c.name categoryName from dish d join category c on d.category_id = c.id
d.name like concat('%',#{name},'%') and category_id = #{categoryId} and d.status = #{status}
order by d.create_time desc
